from django.contrib.auth.password_validation import MinimumLengthValidator, ValidationError, ngettext

class MinimumLengthValidator(MinimumLengthValidator):


    def validate(self, password, user=None):
        if len(password) < self.min_length:
            raise ValidationError(
                ngettext(
                    "This passwordqwe123 is too short. It must contain at least "
                    "%(min_length)d character.",
                    "This passwordqweqw is too short. It must contain at least "
                    "%(min_length)d characters.",
                    self.min_length,
                ),
                code="password_too_short",
                params={"min_length": self.min_length},
            )